import React, { useState } from 'react';
import {Link} from 'react-router-dom';

const CreateCount =() => {
  const [usuario, setUsuario]= useState({
    nombre:'',
    email:'',
    password:'',
    confirmar:'',
  })
  const {nombre, email, password, confirmar} = usuario;
  const onChange = (e) => {
    setUsuario({
      ...usuario, [e.target.name]: e.target.value
    }) 
  }
  const crearCuenta = async () =>{
    const data = {
      nombre: usuario.nombre,
      email: usuario.email,
      password: usuario.password
    }
    console.log(data);
  }
  const onSubmit = (e) => {
    e.preventDefault();
    crearCuenta();
  }

    return (
      <main className="container mx-auto mt-5 md:mt-20 p-5 md:flex md:justify-center">
        <div className="md:w-2/3 lg:w-1/2">
          <h1 className="inline bg-gradient-to-r from-indigo-200 via-violet-700 to-indigo-200 bg-clip-text font-display text-5xl tracking-tight text-transparent">
                G13 Iniciar sesi√≥n Ecommerce
          </h1>
          <form 
            onSubmit={onSubmit}
            className="my-10 bg-white shadow-orange-500 rounded-lg p-10">
            <div my-5>
              <label className="uppercase text-gray-600 block text-xl font-bold">Nombre</label>
              <input
              type="nombre" 
                id="nombre"
                name="nombre"
                placeholder="Ingrese su Nombre"
                className="w-full mt-3 p-3 border rounder-lg bg-gray-50" value={nombre} onChange={onChange}
              />
              <label className="uppercase text-gray-600 block text-xl font-bold">Email</label>
              <input
              type="email" 
                id="email"
                name="email"
                placeholder="Ingrese su Correo"
                className="w-full mt-3 p-3 border rounder-lg bg-gray-50" value={email} onChange={onChange}
              />
              <label className="uppercase text-gray-600 block text-xl font-bold">Password</label>
              <input
              type="password" 
                id="password"
                name="password"
                placeholder="Password"
                className="w-full mt-3 p-3 border rounder-lg bg-gray-50" value={password} onChange={onChange}
              />
              <label className="uppercase text-gray-600 block text-xl font-bold">Confirmacion Password</label>
              <input
              type="confirmar" 
                id="confirmar"
                name="confirmar"
                placeholder="Confirmar Password"
                className="w-full mt-3 p-3 border rounder-lg bg-gray-50" value={confirmar} onChange={onChange}
              />
              <input
                type="submit"
                value="Crear Cuenta"
                className="bg-violet-600 mb-5 w-full py-3 text-white uppercase font-bold rounded hover:cursor-pointer hover:bg-sky-800 transition-colors my-3"
            />
            <Link to ={"/"} className="block text-center my-5 text-violet-600 uppercase text-sm">Regresar</Link>
            </div>
          </form>
        </div>
        
      </main>  
    );
} 

export default CreateCount;
