import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import swal from 'sweetalert'; 
import crud from "../conexiones/crud.js";


const CrearCuenta = () => {
    
  const [usuario, setUsuario] = useState({
    nombre:'',
    email:'',
    password:'',
    confirmar:''
  })

  const { nombre, email, password, confirmar} = usuario;

  const onChange = (e) =>{
    setUsuario({
      ...usuario,
      [e.target.name]: e.target.value
    })
  }

  const crearCuenta = async () =>{
    //los dos password deben ser iguales
    if(password !== confirmar){
      console.log("son diferentes");
      const mensaje = "Las contrase침as son diferentes.";
      swal({
        title: 'Error',
        text: mensaje,
        icon: 'error',
        buttons:{
          confirm:{
            text:'OK',
            value: true,
            visible: true,
            className: 'btn btn-danger',
            closeModal: true
          }
        }
      })
    }else{
      const data = {
        nombre: usuario.nombre,
        email: usuario.email,
        password: usuario.password
      }
      console.log(data);
      const response = await crud.POST(`/api/usuarios`, data);
      const mensaje = response.msg;
      if (mensaje == "El usuario ya existe"){
        const mensaje = "El usuario ya existe.";
      swal({
        title: 'Error',
        text: mensaje,
        icon: 'error',
        buttons:{
          confirm:{
            text:'OK',
            value: true,
            visible: true,
            className: 'btn btn-danger',
            closeModal: true
          }
        }
      })
      }else{

      }
    }
  }

  const onSubmit = (e) => {
    e.preventDefault();
    crearCuenta();
  }

  return (
    
      <main className='container mx-auto mt-5 md:mt-20 p-5 md:flex md:justify-center'>
      <div className='md:w-2/3 lg:w-2/5'>
         <h1 className="inline bg-gradient-to-r from-indigo-200 via-violet-700 to-indigo-200 bg-clip-text font-display text-5xl tracking-tight text-transparent">
           G13 Iniciar sesi칩n Ecommerce
         </h1>

          <form 
            onSubmit={onSubmit}
            className='my-10 bg-white shadow rounded-lg p-10'
          >
           <div className='my-5'>
           <label className='uppercase text-gray-600 block text-xl font-bold' >Nombre</label>
             <input
               type="nombre"
               id="nombre"
               name="nombre"
               placeholder='Ingrese su nombre'
               className='w-full mt-3 p-3 border rounded-lg bg-gray-50'
               value={nombre}
               onChange={onChange}
             />
             
             <label className='uppercase text-gray-600 block text-xl font-bold' >Email</label>
             <input
               type="email"
               id="email"
               name="email"
               placeholder='Email de Registro'
               className='w-full mt-3 p-3 border rounded-lg bg-gray-50'
               value={email}
               onChange={onChange}
             />

         <label className='uppercase text-gray-600 block text-xl font-bold' >password</label>
             <input
               type="password"
               id="password"
               name="password"
               placeholder='Password de Registro'
               className='w-full mt-3 p-3 border rounded-lg bg-gray-50'
               value={password}
               onChange={onChange}
             />

            <label className='uppercase text-gray-600 block text-xl font-bold' >confirmaci칩n </label>
             <input
               type="password"
               id="confirmar"
               name="confirmar"
               placeholder='Confirme su Password'
               className='w-full mt-3 p-3 border rounded-lg bg-gray-50'
               value={confirmar}
               onChange={onChange}
             />
           </div>

           <input 
             type="submit"
             value="Crear Cuenta"
             className="bg-violet-600 mb-5 w-full py-3 text-white uppercase font-bold rounded hover:cursor-pointer hover:bg-violet-400 transition-colors"
         />
         <Link 
         to={"/"}
         className="block text-center my-5 text-violet-600 uppercase text-sm"
         >Regresar</Link>

         </form>

      </div>
   </main> 
   
    );

}

export default CrearCuenta;
